type ToolIconProps = {
  name: ToolIconName;
  className?: string;
};

export type { ToolIconName };

export default function ToolIcon({ name, className }: ToolIconProps): ReactElement {
  return (
    <span
      aria-hidden="true"
      className={className ? `tool-icon ${className}` : "tool-icon"}
      dangerouslySetInnerHTML={{ __html: ICONS[name] }}
    />
  );
}
